@use '../abstracts'as *;

.navbar {
    position: fixed;
    top: 0;
    width: 100%;
    height: 125px;
    left: 0;
    z-index: 10;
    padding: 8px 16px;
    align-items: center;

    @media only screen and (max-width: 768px) {
        padding: 0px 5px;
    }

    ul {
        display: flex;
        width: 100%;
        justify-content: flex-end;
        padding-right: 25px;

        @media only screen and (max-width: 768px) {
            align-items: flex-end;
            padding-right: 0px;
        }
    }

    li {
        padding: 0px 15px;

        @media only screen and (max-width: 768px) {
            background: $olive;
            opacity: 0.6;
        }

    }

    &__logo {
        display: flex;
        justify-content: flex-start;
        height: 100px;
        width: 100px;
        margin-left: 0px 40px;

        @media only screen and (min-width: 768px) {
            margin-left: 0px;
        }
    }

    &__button {
        @include font-raleway;
        background: none;
        border: none;

        &:focus {
            outline: none;
        }

        &:hover {
            color: $green;
        }
    }
}

.navbar-toggler {
    padding-right: 20px;

    @media only screen and (min-width: 768px) {
        display: none;
    }
}

.navbar-toggler:visited {
    border: none;
}

